  const newValue = e.target.value
}


  return (
    <div className="container">
      <h1>Hello</h1>
      <form>
        <input
          onChange="handleChange"
          name="fName"
          placeholder="First Name"
          value={fullName.fName}
          />


        <input
              onChange="handleChange"
              name="lName"
              placeholder="Last Name"
              value={fullName.lName}
              />
